-- -----------------------------------------------------
-- procedure LlenadoUsuarios
-- -----------------------------------------------------

DELIMITER $$
USE `edumatch2`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `LlenadoUsuarios`( IN cantidad INT)
BEGIN 
   DECLARE i INT DEFAULT 1;
   DECLARE NomUs varchar(50);
   declare apellidoM varchar(50);
   declare apellidoP varchar(50);
   declare edad int;
   declare contraseña varchar(255);
   declare correo varchar(100);
   declare tel varchar(15);
   declare rol varchar(50);
   
   
   while   i< cantidad do
   set apellidoP = case round(rand()*5)
   when 0 then 'ramos'
   when 1 then 'jimenez'
   when 2 then 'santes'
   when 3 then 'ramirez'
   when 4 then 'saldado'
   when 5 then 'ocpaco'
   else 'reyes'
   end;
   
   set apellidoM = case round(rand()*5)
   when 0 then 'lorenzo'
   when 1 then 'cortes'
   when 2 then 'castillo'
   when 3 then 'islas'
   when 4 then 'castañeda'
   when 5 then 'romero'
   else 'ibarra'
   end;
   
   set NomUs = case round(rand()*5)
   when 0 then 'alan'
   when 1 then 'mario'
   when 2 then 'saulo'
   when 3 then 'daniel'
   when 4 then 'obed'
   when 5 then 'dimauro'
   else 'manuel'
   end;
   
   
   set edad = case round(rand()*5)
   when 0 then '19'
   when 1 then '20'
   when 2 then '21'
   when 3 then '22'
   when 4 then '23'
   when 5 then '18'
   else '17'
   end;
   
   set contraseña  = case round(rand()*5)
   when 0 then 'abcdskd'
   when 1 then '121232o2d'
   when 2 then '99ds9dis'
   when 3 then 'dsidas9'
   when 4 then '09ds9isda'
   when 5 then 'sdasd92'
   else '0sdasd9'
   end;
   
   set correo  = concat('usuario_',i,'@utxicotepec.edu');
   set tel = concat('7849875678',i);
	
    
	set rol = case round(rand()*2)
   when 0 then 'Usuario'
   when 1 then 'Administrador'
   else 'Usuario'
   end;
   
insert into usuario (Nombre,Apellido_P,Apellido_M,Edad,Contraseña,Correo,Telefono,Rol) values (NomUs,apellidoM,apellidoP,edad,contraseña,correo,tel,rol);
set i=i+1;
end while;
end$$

