-- -----------------------------------------------------

DELIMITER $$
USE `edumatch2`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `eliminarUsuario`(
    IN pIdUsuario INT
)
BEGIN
    DELETE FROM usuario
    WHERE idUsuario = pIdUsuario;
END$$

DELIMITER ;

